mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
SELECT s.*, c.name as category_name FROM setmeal s left outer join category c on s.category_id = c.id


        and s.category_id = #{categoryId}


        and s.name LIKE CONCAT('%', #{name}, '%')


        and s.status = #{status}


ORDER BY s.create_time DESC

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
